        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (1, 'Ver muito lucidamente prejudica o sentir demasiado. E os gregos viam muito lucidamente. Por isso pouco sentiam. Daí a sua perfeita execução da obra de arte.')");
