        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'A verdadeira medida de um homem não se vê na forma como se comporta em momentos de conforto e conveniência, mas em como se mantém em tempos de controvérsia e desafio.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'Aprendemos a voar como os pássaros e a nadar como os peixes, mas não aprendemos a conviver como irmãos.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'Não ficaremos satisfeitos enquanto um só negro do Mississipi não puder votar ou um negro de Nova York acreditar que não tem razão para votar.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'O perdão é um catalisador que cria a ambiência necessária para uma nova partida, para um reinício.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'Uma das coisas importantes da não violência é que não busca destruir a pessoa, mas transformá-la.')");
