        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'Todo o progresso é precário, e a solução para um problema coloca-nos diante de outro problema.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'O tumulto é a linguagem daqueles que ninguém entende.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'A injustiça num lugar qualquer é uma ameaça à justiça em todo o lugar.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'O que me preocupa não é o grito dos maus. É o silêncio dos bons.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'Se um homem não descobriu nada pelo qual morreria, não está pronto para viver.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'No final, não nos lembraremos das palavras dos nossos inimigos, mas do silêncio dos nossos amigos.')");
        db.execSQL("INSERT INTO " + QUOTE_TABLE + "(" + _ID_AUTHOR_QUOTE + "," + CONTENT + ") VALUES (27, 'Pouca coisa é necessária para transformar inteiramente uma vida: amor no coração e sorriso nos lábios.')");
